import React from "react";

function App(){
  return <div>
    {/*Component composition*/}
    <Logo />
    <BestPokemon />
    <CaughtPokemon />
      </div>;
}

// React Arrow Function
const Logo = () => {
  // Embedding JS into JSX
  const appName = "Daisy's Pokedex";
  return <header>
    <h1>Welcome to {appName} Pokedex</h1>
    <img src="https://assets.pokemon.com/assets/cms2/img/pokedex/full/006.png"
      alt="pokemon"/>
      </header>;
};

// React Arrow functioans
const BestPokemon = () => {
  const abilities = ["Blaze", "Solar Power"];
  return (<div>
  <p>My favorite Pokemon is Charizard</p>
  <p>Species: Flame Pokemon</p>
  <p>Height: 1.7m (5'07")</p>
  <p>Weight: 90.5kg (199.5lbs)</p>
  <p>Powers:</p>
  <ul>{abilities.map((ability) => (
    <li>{ability}</li>
  ))}</ul>
  </div>);
};

const CaughtPokemon = () => {
  const date = new Date().toLocaleString();
  return <p>Caught 0 Pokemon on {date}</p>;
};

export default App;
